mapper.select:
SELECT id id, ord_code ordCode, order_item_id orderItemId, mac_code macCode, item_color itemColor, item_owner itemOwner, item_variety itemVariety, item_cgy_code itemCgyCode, item_yc_type itemYcType, item_yb_type itemYbType, item_total_sq itemTotalSq, item_total_weight itemTotalWeight, item_num itemNum, item_thick itemThick, item_width itemWidth, item_lenth itemLenth, item_code itemCode, create_time createTime FROM t_history_prod_record
and ord_code like concat('%', concat(#{ordCode,jdbcType=VARCHAR}, '%'))
and create_time between #{startDate} and #{endDate}
and mac_code = #{macCode,jdbcType=VARCHAR}
and item_color = #{itemColor,jdbcType=VARCHAR}
and item_variety = #{itemVariety,jdbcType=BIGINT}
and item_cgy_code = #{itemCgyCode,jdbcType=BIGINT}
and item_yc_type = #{itemYcType,jdbcType=VARCHAR}
and item_yb_type = #{itemYbType,jdbcType=VARCHAR}
and item_code = #{itemCode,jdbcType=VARCHAR}
