mapper.select:
        SELECT
        osd.bar_code barCode,
        bi.ord_code ordCode,
        bi.item_id itemId
        FROM
        T_OUT_STOCK_DETAIL osd
        LEFT JOIN T_BARCODE_INFO bi ON osd.bar_code = bi.bar_code
        
            
                osd.out_code = #{outCode,jdbcType=VARCHAR}
            
            
                bi.ord_code = #{ordCode,jdbcType=VARCHAR}
            
            
                bi.item_id = #{itemId,jdbcType=VARCHAR}
