mapper.select:
SELECT ppd.id prodPlanDetailId, ppd.prod_plan_code prodPlanCode, ppd.ord_code ordCode, ppd.order_item_id orderItemId, ppd.item_code itemCode, ppd.item_lenth itemLenth, ppd.item_width itemWidth, ppd.item_thick itemThick, ppd.item_num itemNum, ppd.item_total_weight itemTotalWeight, ppd.item_total_sq itemTotalSq, ppd.item_yb_type itemYbType, ppd.item_yc_type itemYcType, ppd.item_status itemStatus, ppd.item_cgy_code itemCgyCode, ppd.item_variety itemVariety, ppd.item_unit itemUnit, ppd.item_owner itemOwner, ppd.item_color itemColor, ppd.mac_code macCode, pp.formula formula FROM t_prod_plan_detail ppd left join t_prod_plan pp on ppd.prod_plan_code = pp.prod_plan_code
and ppd.id = #{prodPlanDetailId,jdbcType=BIGINT}
and ppd.item_status = #{itemStatus,jdbcType=CHAR}
and ppd.item_cgy_code = #{itemCgyCode,jdbcType=BIGINT}
and ppd.item_variety = #{itemVariety,jdbcType=BIGINT}
and ppd.mac_code = #{macCode,jdbcType=VARCHAR}
and ppd.item_color = #{itemColor,jdbcType=VARCHAR}
order by mac_code,item_variety,item_color,item_width desc
