mapper.select:
SELECT td.id, tc.cus_name, td.prod_plan_code, td.ord_code, td.order_item_id, td.item_code, td.item_lenth, td.item_width, td.item_thick, td.item_num, td.item_total_weight, td.item_total_sq, td.item_status, td.item_cgy_code, td.item_variety, td.item_unit, td.item_owner, td.item_color, td.mac_code, td.item_price_type, td.item_remarks, oi.item_yb_type, oi.item_yc_type, od.ord_title, td.threshold_up, td.threshold_down, ifnull(tp.prod_density,0) itemDensity FROM t_prod_plan_detail td inner join T_ORDER od on od.ord_code = td.ord_code inner join t_order_item oi on td.order_item_id = oi.id left join t_customer tc on tc.cus_code = od.cus_code inner join t_product tp on oi.item_code = tp.prod_code WHERE td.prod_plan_code in
#{code}
and td.item_width >= #{startLength}
and td.item_width <= #{endLength}
and tc.cus_name like concat('%',#{cusName},'%')
and od.ord_title like concat('%',#{orderTitle},'%')
ORDER BY td.item_status, td.item_width DESC

SELECT td.id, td.prod_plan_code, td.ord_code, td.order_item_id, td.item_code, td.item_lenth, td.item_width, td.item_thick, td.item_num, td.item_total_weight, td.item_total_sq, td.item_status, td.item_cgy_code, td.item_variety, td.item_unit, td.item_owner, td.item_color, td.mac_code, td.item_price_type, td.item_remarks, oi.item_yb_type, oi.item_yc_type, od.ord_title, td.threshold_up, td.threshold_down, ifnull(tp.prod_density,0) itemDensity FROM t_prod_plan_detail td inner join T_ORDER od on od.ord_code = td.ord_code inner join t_order_item oi on td.order_item_id = oi.id inner join t_product tp on oi.item_code = tp.prod_code
and td.item_cgy_code = #{prodCgyCode,jdbcType=BIGINT}
and td.item_variety = #{prodVariety,jdbcType=BIGINT}
and td.item_width >= #{widthMin,jdbcType=BIGINT}
and td.item_width <= #{widthMax,jdbcType=BIGINT}
and td.item_color = #{prodColor,jdbcType=VARCHAR}
and od.cus_code = #{cusCode,jdbcType=VARCHAR}
and oi.create_time >= #{startDate,jdbcType=VARCHAR}
and oi.create_time <= #{endDate,jdbcType=VARCHAR}
ORDER BY td.item_status, td.item_width DESC

