mapper.select:
SELECT *
FROM T_PRODUCT

    
        and prod_code = #{prodCode}
    
    
        and prod_name like concat(concat('%', #{prodName}),'%')
    
    
        and prod_cgy_code = #{prodCgyCode}
    
    
        and prod_thick = #{prodThick}
    
    
        and prod_variety = #{prodVariety}
    
    
        and prod_price_type = #{prodPriceType}
    
    
        and prod_unit = #{prodUnit}
