mapper.select:
SELECT s.id stockId, s.bar_code barCode, s.ord_code ordCode, o.ord_title orderTitle, s.item_code itemCode, s.item_lenth itemLenth, s.item_width itemWidth, s.item_thick itemThick, s.item_weight itemWeight, s.item_sq itemSq, s.item_owner itemOwner, s.item_yc_type itemYcType, s.item_yb_type itemYbType, s.item_cgy_code itemCgyCode, s.item_variety itemVariety, s.item_unit itemUnit, s.item_color itemColor, s.mac_code macCode FROM t_stock s left join t_order o on s.ord_code = o.ord_code
and s.item_cgy_code = #{itemCgyCode,jdbcType=BIGINT}
and s.item_variety = #{itemVariety,jdbcType=BIGINT}
and s.ord_code like concat('%', #{ordCode,jdbcType=VARCHAR}, '%')
and s.create_time between #{startDate} and #{endDate}
order by create_time desc

select s.* from t_out_stock_detail osd
left join t_stock s on osd.bar_code = s.bar_code
where osd.out_code = #{outCode}
